def test_forecast_RandomForestRegressor():
    client_id = 'AR21TY2023'
    model_id = 8

    import pandas as pd

    data = pd.read_csv('test_store_sizing_data.csv')

    forecast_period = 720

    data['StartDate'] = pd.to_datetime(data['StartDate'])
    data.shape

    data.reset_index(inplace=True)
    data.rename(columns={'StartDate': 'ds', 'Tickets': 'x', 'Checkouts': 'y'}, inplace=True)    

    # drop y
    #X = data.iloc[:,:-1].values
    X = data.drop(columns=['y'])

    # forecasting period input data
    X_pred = X[-forecast_period:]

    ds = X_pred['ds'].astype('string').tolist()
    x = X_pred['x'].tolist()

    #print(ds)
    #print(x)

    assert len(ds) == forecast_period
    assert len(x) == forecast_period

    model_input_data = {'ds' : ds, 'x': x}

    response = client.post(f'{request_prefix}/models/{model_id}/forecast', headers={'Authorization': 'Bearer ' + settings.JWT_TOKEN}, json={'client_id': client_id, 'model_input_data': model_input_data, 'forecast_period': forecast_period})

    print(response.json())
    
    assert response.status_code == 200
    assert len(response.json()['ds']) == forecast_period

    forecast = response.json()['forecast']
    assert len(forecast) == forecast_period

    for element in forecast:
        print(element)
        assert isinstance(element, float) or isinstance(element, int)

    assert ds == response.json()['ds']

