def test_forecast_MLPRegressor_2_variable():
    client_id = 'AR21TY2023'
    model_id = 9

    import pandas as pd

    data = pd.read_csv('test_store_tickets_and_sizing_data.csv')

    forecast_period = 720
    format = "%Y-%m-%d %H:%M:%S"    

    data['ds'] = pd.to_datetime(data['ds'])
    data.shape

    ds = data['ds'].astype('string').tolist()
    y1 = data['y1'].tolist()
    y2 = data['y2'].tolist()

    last_datetime = datetime.strptime(ds[len(ds)-forecast_period-1], format)
    curr_datetime = datetime.strptime(ds[len(ds)-forecast_period], format)
    next_datetime = datetime.strptime(ds[len(ds)-forecast_period+1], format)

    ds = ds[-forecast_period * 2:-forecast_period]
    y1 = y1[-forecast_period * 2:-forecast_period]
    y2 = y2[-forecast_period * 2:-forecast_period]

    #print(ds)
    #print(y1)
    #print(y2)

    assert len(ds) == forecast_period
    assert len(y1) == forecast_period
    assert len(y2) == forecast_period

    model_input_data = {'ds' : ds, 'y1': y1, 'y2': y2}

    response = client.post(f'{request_prefix}/models/{model_id}/forecast', headers={'Authorization': 'Bearer ' + settings.JWT_TOKEN}, json={'client_id': client_id, 'model_input_data': model_input_data, 'forecast_period': forecast_period, 'customer_flow_and_team_size': True})

    print(response.json())
    
    assert response.status_code == 200
    assert len(response.json()['ds']) == forecast_period

    forecasts = response.json()['forecasts']
    assert len(forecasts['y1']) == forecast_period
    assert len(forecasts['y2']) == forecast_period

    for element in forecasts['y1']:
        print(element)
        assert isinstance(element, float) or isinstance(element, int)

    for element in forecasts['y2']:
        print(element)
        assert isinstance(element, float) or isinstance(element, int)

    ds = response.json()['ds']

    assert last_datetime + timedelta(hours = forecast_period) == datetime.strptime(ds[len(ds)-1], format)
    assert curr_datetime == datetime.strptime(ds[0], format)
    assert next_datetime == datetime.strptime(ds[1], format)

